                      </SelectContent>
                    </Select>
                  </div>
                </div>
                <Button onClick={handleCreateUser} disabled={loading} className="mt-4 bg-red-600 hover:bg-red-700">
                  <Save className="w-4 h-4 mr-2" /> Create User
                </Button>
              </Card>
