import { motion } from "framer-motion";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { LucideIcon } from "lucide-react";

interface NeonCardProps {
  title: string;
  description?: string;
  icon?: LucideIcon;
  children?: React.ReactNode;
  onClick?: () => void;
  variant?: "default" | "green" | "blue" | "purple" | "gradient";
  className?: string;
  animated?: boolean;
  glowing?: boolean;
}

export default function NeonCard({
  title,
  description,
  icon: Icon,
  children,
  onClick,
  variant = "default",
  className = "",
  animated = true,
  glowing = false,
}: NeonCardProps) {
  const variants = {
    default: {
      border: "border-border/50",
      glow: "",
      icon: "bg-primary/20 text-primary",
      gradient: "from-primary/10 to-transparent",
    },
    green: {
      border: "border-primary/30",
      glow: "neon-glow",
      icon: "bg-primary/20 text-primary",
      gradient: "from-primary/20 to-transparent",
    },
    blue: {
      border: "border-accent/30",
      glow: "neon-glow-blue",
      icon: "bg-accent/20 text-accent",
      gradient: "from-accent/20 to-transparent",
    },
    purple: {
      border: "border-neon-purple/30",
      glow: "neon-glow-purple",
      icon: "bg-neon-purple/20 text-neon-purple",
      gradient: "from-neon-purple/20 to-transparent",
    },
    gradient: {
      border: "border-primary/30",
      glow: "",
      icon: "bg-gradient-to-br from-primary to-accent text-white",
      gradient: "from-primary/10 via-accent/10 to-transparent",
    },
  };

  const style = variants[variant];

  const cardContent = (
    <Card
      className={`
        relative overflow-hidden
        bg-card/80 backdrop-blur-sm
        border ${style.border}
        transition-all duration-300
        ${onClick ? "cursor-pointer" : ""}
        ${glowing ? style.glow : ""}
        ${className}
      `}
      onClick={onClick}
    >
      <div 
        className={`absolute inset-0 bg-gradient-to-br ${style.gradient} pointer-events-none`} 
      />

      <CardHeader className="relative space-y-4">
        {Icon && (
          <div className={`w-14 h-14 rounded-xl ${style.icon} flex items-center justify-center`}>
            <Icon className="w-7 h-7" />
          </div>
        )}
        <div className="space-y-1">
          <CardTitle className="text-xl font-display">{title}</CardTitle>
          {description && (
            <CardDescription className="text-sm">{description}</CardDescription>
          )}
        </div>
      </CardHeader>

      {children && (
        <CardContent className="relative">
          {children}
        </CardContent>
      )}
    </Card>
  );

  if (animated) {
    return (
      <motion.div
        whileHover={{ scale: 1.02, y: -5 }}
        whileTap={{ scale: 0.98 }}
        transition={{ type: "spring", stiffness: 300, damping: 20 }}
      >
        {cardContent}
      </motion.div>
    );
  }

  return cardContent;
}

import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import NotFound from "@/pages/not-found";
import Dashboard from "@/pages/Dashboard";
import WorkoutsPage from "@/pages/WorkoutsPage";
import PlansPage from "@/pages/PlansPage";
import NutritionPage from "@/pages/NutritionPage";
import ProgressPage from "@/pages/ProgressPage";
import ProfilePage from "@/pages/ProfilePage";
import AdminPage from "@/pages/AdminPage";
import BodyMapPage from "@/pages/BodyMapPage";
import StreakPage from "@/pages/StreakPage";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Dashboard} />
      <Route path="/workouts" component={WorkoutsPage} />
      <Route path="/plans" component={PlansPage} />
      <Route path="/nutrition" component={NutritionPage} />
      <Route path="/progress" component={ProgressPage} />
      <Route path="/profile" component={ProfilePage} />
      <Route path="/admin" component={AdminPage} />
      <Route path="/bodymap" component={BodyMapPage} />
      <Route path="/streak" component={StreakPage} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

