@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&family=Orbitron:wght@400;500;600;700;800;900&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);
  --opaque-button-border-intensity: 9;
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 20% 6%;
  --foreground: 0 0% 95%;
  --border: 220 15% 15%;

  --card: 220 20% 8%;
  --card-foreground: 0 0% 95%;
  --card-border: 220 15% 12%;

  --sidebar: 220 20% 10%;
  --sidebar-foreground: 0 0% 95%;
  --sidebar-border: 220 15% 13%;
  --sidebar-primary: 160 100% 50%;
  --sidebar-primary-foreground: 220 20% 6%;
  --sidebar-accent: 220 15% 13%;
  --sidebar-accent-foreground: 0 0% 95%;
  --sidebar-ring: 160 100% 50%;

  --popover: 220 20% 10%;
  --popover-foreground: 0 0% 95%;
  --popover-border: 220 15% 14%;

  --primary: 160 100% 50%;
  --primary-foreground: 220 20% 6%;

  --secondary: 220 15% 14%;
  --secondary-foreground: 0 0% 95%;

  --muted: 220 15% 15%;
  --muted-foreground: 220 10% 65%;

  --accent: 200 100% 50%;
