  --destructive: 0 84% 50%;
  --destructive-foreground: 0 0% 98%;

  --input: 220 15% 20%;
  --ring: 160 100% 50%;

  --chart-1: 160 100% 50%;
  --chart-2: 200 100% 50%;
  --chart-3: 280 100% 60%;
  --chart-4: 320 100% 55%;
  --chart-5: 40 100% 55%;

  --neon-green: 160 100% 50%;
  --neon-blue: 200 100% 50%;
  --neon-purple: 280 100% 60%;
  --neon-pink: 320 100% 55%;

  --font-sans: Inter, -apple-system, BlinkMacSystemFont, sans-serif;
  --font-display: Orbitron, Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .625rem;

  --shadow-2xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 1px 2px -1px hsl(0 0% 0% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 1px 2px -1px hsl(0 0% 0% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 2px 4px -1px hsl(0 0% 0% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 4px 6px -1px hsl(0 0% 0% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 8px 10px -1px hsl(0 0% 0% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --primary-border: hsl(var(--primary));
  --secondary-border: hsl(var(--secondary));
  --muted-border: hsl(var(--muted));
  --accent-border: hsl(var(--accent));
  --destructive-border: hsl(var(--destructive));
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background-image: 
      radial-gradient(ellipse 80% 50% at 50% -20%, hsla(160, 100%, 50%, 0.15), transparent),
      radial-gradient(ellipse 60% 40% at 100% 0%, hsla(200, 100%, 50%, 0.1), transparent),
      radial-gradient(ellipse 50% 30% at 0% 100%, hsla(280, 100%, 60%, 0.08), transparent);
    background-attachment: fixed;
  }
}

@layer utilities {
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }

  .neon-glow {
    box-shadow: 
      0 0 5px hsl(var(--primary)),
      0 0 10px hsl(var(--primary) / 0.5),
      0 0 20px hsl(var(--primary) / 0.3),
      0 0 40px hsl(var(--primary) / 0.1);
  }

  .neon-glow-blue {
    box-shadow: 
      0 0 5px hsl(var(--accent)),
      0 0 10px hsl(var(--accent) / 0.5),
      0 0 20px hsl(var(--accent) / 0.3),
      0 0 40px hsl(var(--accent) / 0.1);
  }

  .neon-glow-purple {
    box-shadow: 
      0 0 5px hsl(var(--neon-purple)),
      0 0 10px hsl(var(--neon-purple) / 0.5),
      0 0 20px hsl(var(--neon-purple) / 0.3),
      0 0 40px hsl(var(--neon-purple) / 0.1);
  }

  .neon-text {
    text-shadow: 
      0 0 5px hsl(var(--primary)),
      0 0 10px hsl(var(--primary) / 0.5),
      0 0 20px hsl(var(--primary) / 0.3);
  }

  .neon-text-blue {
    text-shadow: 
      0 0 5px hsl(var(--accent)),
      0 0 10px hsl(var(--accent) / 0.5),
      0 0 20px hsl(var(--accent) / 0.3);
  }

  .neon-border {
    border: 1px solid hsl(var(--primary) / 0.5);
    box-shadow: 
      inset 0 0 10px hsl(var(--primary) / 0.1),
      0 0 10px hsl(var(--primary) / 0.2);
  }

  .neon-border-blue {
    border: 1px solid hsl(var(--accent) / 0.5);
    box-shadow: 
      inset 0 0 10px hsl(var(--accent) / 0.1),
      0 0 10px hsl(var(--accent) / 0.2);
  }

  .gradient-bg {
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%,
      hsl(220 20% 8%) 50%,
      hsl(var(--background)) 100%);
  }

  .gradient-card {
    background: linear-gradient(145deg, 
      hsl(var(--card)) 0%,
      hsl(220 20% 10%) 100%);
  }

  .glass-effect {
    background: hsl(var(--card) / 0.8);
    backdrop-filter: blur(10px);
    border: 1px solid hsl(var(--border) / 0.5);
  }

  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  @keyframes glow-pulse {
    0%, 100% { 
      box-shadow: 
        0 0 5px hsl(var(--primary)),
        0 0 10px hsl(var(--primary) / 0.5);
    }
    50% { 
      box-shadow: 
        0 0 10px hsl(var(--primary)),
        0 0 20px hsl(var(--primary) / 0.7),
        0 0 30px hsl(var(--primary) / 0.4);
    }
  }

  .animate-glow-pulse {
    animation: glow-pulse 2s ease-in-out infinite;
  }

  .font-display {
    font-family: var(--font-display);
  }
}

