interface ResultType {
  type: string;
  title: string;
  description: string;
  icon: any;
  color: string;
  bgColor: string;
  tips: string[];
}

const resultTypes: Record<string, ResultType> = {
  "fat_loss_beginner": {
    type: "fat_loss_beginner",
    title: "Fat Loss Beginner",
