const MusclePath = ({ 
  d, 
  id, 
  name, 
  isActive, 
  onClick
}: { 
  d: string; 
  id: string; 
  name: string; 
  isActive: boolean; 
  onClick: (id: string) => void;
}) => (
  <motion.path
    d={d}
    className={cn(
      "cursor-pointer transition-all duration-300 stroke-[0.5px]",
      isActive 
        ? "fill-primary/60 stroke-primary filter drop-shadow-[0_0_10px_rgba(0,255,157,0.6)]" 
