interface DailyLog {
  date: string;
  entries: FoodEntry[];
  caloriesConsumed: number;
  proteinConsumed: number;
  carbsConsumed: number;
  fatsConsumed: number;
}

const foodDatabase: Record<string, { protein: number; carbs: number; fats: number; calories: number; defaultUnit: string; servingSize: number }> = {
