@import "tailwindcss";

@plugin "tailwindcss-animate";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --font-display: 'Orbitron', sans-serif;
  --font-body: 'Outfit', sans-serif;
  --font-mono: 'Rajdhani', monospace;

  --color-background: hsl(240 10% 4%);
  --color-foreground: hsl(0 0% 98%);

  --color-card: hsl(240 10% 6%);
  --color-card-foreground: hsl(0 0% 98%);

  --color-popover: hsl(240 10% 6%);
  --color-popover-foreground: hsl(0 0% 98%);

  --color-primary: hsl(150 100% 50%); /* Neon Green */
  --color-primary-foreground: hsl(240 10% 4%);

  --color-secondary: hsl(270 100% 60%); /* Neon Purple */
  --color-secondary-foreground: hsl(0 0% 98%);

  --color-muted: hsl(240 10% 12%);
  --color-muted-foreground: hsl(240 5% 65%);

  --color-accent: hsl(190 100% 50%); /* Cyber Blue */
  --color-accent-foreground: hsl(240 10% 4%);

  --color-destructive: hsl(0 85% 60%);
  --color-destructive-foreground: hsl(0 0% 98%);

  --color-border: hsl(240 10% 15%);
  --color-input: hsl(240 10% 15%);
  --color-ring: hsl(150 100% 50%);

  --color-chart-1: hsl(150 100% 50%);
  --color-chart-2: hsl(270 100% 60%);
  --color-chart-3: hsl(190 100% 50%);
  --color-chart-4: hsl(45 100% 50%);
  --color-chart-5: hsl(320 100% 60%);

  --radius-sm: 0.25rem;
  --radius-md: 0.5rem;
  --radius-lg: 0.75rem;

  --animate-glow: glow 2s ease-in-out infinite alternate;

  @keyframes glow {
    from {
      box-shadow: 0 0 5px hsla(150, 100%, 50%, 0.2);
    }
    to {
      box-shadow: 0 0 20px hsla(150, 100%, 50%, 0.6);
    }
  }
}

@layer base {
  :root {
    color-scheme: dark;
  }

  * {
    @apply border-border;
  }
  body {
    @apply font-body antialiased bg-background text-foreground;
    background-image: 
      radial-gradient(circle at 50% 0%, hsla(270, 100%, 60%, 0.15), transparent 40%),
      radial-gradient(circle at 0% 50%, hsla(150, 100%, 50%, 0.1), transparent 30%),
      radial-gradient(circle at 100% 50%, hsla(190, 100%, 50%, 0.1), transparent 30%);
    background-attachment: fixed;
    min-height: 100vh;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-display tracking-wider uppercase;
  }
}

@layer utilities {
  .text-neon-green {
    color: hsl(150 100% 50%);
    text-shadow: 0 0 10px hsla(150, 100%, 50%, 0.5);
  }

  .text-neon-purple {
    color: hsl(270 100% 60%);
    text-shadow: 0 0 10px hsla(270, 100%, 60%, 0.5);
