                className={selectedDay?.log?.dietDone ? "bg-green-600" : "border-white/10"}
                onClick={() => updateDayLog('diet')}
                disabled={selectedDay?.log?.isRestDay}
              >
                {selectedDay?.log?.dietDone ? "Yes" : "No"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </Layout>
  );
}

